    public void metodoVerificarPatas(){
        int quantidade = Integer.parseInt(textoQuantidade.getText());
        String tipo;
        
        switch(quantidade){
            case 1:
                tipo = "Saci";
                break;
            case 2:
                tipo = "Bípede";
                break;
            case 3:
                tipo = "Tripede";
                break;
            case 4:
                tipo = "Quadrupede";
                break;
            default:
                tipo = "Homem branco";
                break;
        }
        
        labelResultado.setText(tipo);
    }//fecha o meu método

